import React, { useState, useEffect } from 'react';

const DisplayResume = () => {
  const userId = 'JTJmdXNlcnByb2ZpbGVzY29udGFpbmVyJTJmNjM4MzcxNjc0MzA1ODY1NDUx'; // Replace with the actual user ID
  const functionUri = 'https://functionappuserresumes.azurewebsites.net/api/HttpTriggerGetResume?code=3xBINMjx9M_ybw4gII-HTDCL5ejk46kuzkfwebsa1gMpAzFu5xSR0w==';

  const [pdfUrl, setPdfUrl] = useState(null);

  useEffect(() => {
    const fetchPdf = async () => {
      try {
        const response = await fetch(`${functionUri}&userId=${userId}`);
        if (!response.ok) {
          throw new Error(`Error fetching PDF: ${response.statusText}`);
        }

        const blob = await response.blob();
        const pdfUrl = URL.createObjectURL(blob);
        setPdfUrl(pdfUrl);
      } catch (error) {
        console.error('Error:', error.message);
      }
    };

    fetchPdf();
  }, [userId, functionUri]);

  return (
    <div>
      {pdfUrl ? (
        <iframe
          title="Resume PDF"
          src={pdfUrl}
          width="100%"
          height="500px"
          style={{ border: 'none' }}
        />
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default DisplayResume;
