import React, { useState } from 'react';
import { postJobListing } from '../api/jobs';
import { useAuth } from '../hooks/useAuth';

const NewJobListing = () => {
  const [title, setTitle] = useState('');
  const [company, setCompany] = useState('');
  const [description, setDescription] = useState('');
  const { user } = useAuth();

  const handlePostJobListing = async () => {
    try {
      if (!user) {
        console.error('User not logged in.');
        return;
      }

      const response = await postJobListing(user.id, {
        Title: title,
        Company: company,
        Description: description,
        LikesCount: 0,
        DislikesCount: 0,
      });

      console.log('Job Listing posted successfully:', response);
      // You can add additional logic or redirect the user after posting the job listing
    } catch (error) {
      console.error('Error posting job listing:', error);
      // Handle errors or display a user-friendly message
    }
  };

  const containerStyle = {
    maxWidth: '400px',
    margin: '0 auto',
    padding: '20px',
    border: '1px solid #ccc',
    borderRadius: '8px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
  };

  const labelStyle = {
    fontWeight: 'bold',
    marginBottom: '8px',
    display: 'block',
  };

  const inputStyle = {
    width: '100%',
    padding: '8px',
    boxSizing: 'border-box',
  };

  const textareaStyle = {
    width: '100%',
    padding: '8px',
    boxSizing: 'border-box',
    minHeight: '100px', // Set a minimum height for the textarea
  };

  const buttonStyle = {
    backgroundColor: '#4caf50',
    color: 'white',
    padding: '10px',
    border: 'none',
    cursor: 'pointer',
  };

  return (
    <div style={containerStyle}>
      <h1>Welcome to Job Posting Page</h1>
      <form style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <div>
          <label htmlFor="title" style={labelStyle}>Job Title:</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            style={inputStyle}
          />
        </div>
        <div>
          <label htmlFor="company" style={labelStyle}>Company:</label>
          <input
            type="text"
            id="company"
            value={company}
            onChange={(e) => setCompany(e.target.value)}
            style={inputStyle}
          />
        </div>
        <div>
          <label htmlFor="description" style={labelStyle}>Job Description:</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            style={textareaStyle}
          />
        </div>
        <button
          type="button"
          onClick={handlePostJobListing}
          style={buttonStyle}
        >
          Post Job Listing
        </button>
      </form>
    </div>
  );
};

export default NewJobListing;
