import React, { useEffect, useState } from 'react';
import { useAuth } from '../hooks/useAuth'; // Replace with the actual import for your authentication library

const UserProfile = () => {
  const { user } = useAuth(); // Assuming useAuth provides user information
  const [userData, setUserData] = useState(null);
  const [imageSrc, setImageSrc] = useState(null); // Add this line

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Use the user ID dynamically in the URL
        const userId = user.id;
        const apiUrl = `https://prod-04.uksouth.logic.azure.com:443/workflows/eee57e98e2974fe78e4576d5b2662762/triggers/manual/paths/invoke?api-version=2016-10-01&sp=%2Ftriggers%2Fmanual%2Frun&sv=1.0&sig=nw56HtR2sYXhX_wdTuaH8gcnmBkMOYRyWF6bZQu_9_s&userId=${userId}`;

        // Perform the API request to fetch user data
        const response = await fetch(apiUrl);
        const contentType = response.headers.get('Content-Type');

        if (contentType && contentType.includes('application/json')) {
          const userData = await response.json();
          setUserData(userData.value[0]);
          setImageSrc(userData.value[0].$content);
        } else {
          // Handle non-JSON response (e.g., display image)
          const imageBlob = await response.blob();
          const imageUrl = URL.createObjectURL(imageBlob);
          setImageSrc(imageUrl);
        }
      } catch (error) {
        console.error('Error fetching user data', error);
      }
    };

    if (user) {
      fetchUserData();
    }
  }, [user]);

  return (
    <div>
      <h2>User Profile</h2>
      {userData ? (
        <div>
          <p>Email: {userData.email}</p>
          <p>Username: {userData.username}</p>
          <p>Password: {userData.password}</p>
          {/* Add more fields as needed */}
        </div>
      ) : (
        <p>Loading user data...</p>
      )}

      {imageSrc && <img src={imageSrc} alt="User" />} {/* Add this line */}
    </div>
  );
};

export default UserProfile;
