import axios from 'axios';

const API_BASE_URL = 'http://localhost:5001';

export const postJobListing = async (jobData) => {
  try {
    console.log("job data", jobData)
    const response = await axios.post(`${API_BASE_URL}/joblistings`, {
      jobData
    } );
    return response.data;
  } catch (error) {
    throw error.response.data;
  }
};

export const getJobListingsByUserId = async (user_id) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/job-listings/?user_id=${user_id}`);
    return response.data;
  } catch (error) {
    throw error.response.data;
  }
};
