import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    profileImage: null, // Store the selected profile image file
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formDataForBackend = new FormData();
      formDataForBackend.append('username', formData.username);
      formDataForBackend.append('email', formData.email);
      formDataForBackend.append('password', formData.password);

      // Get the binary image data
      const binaryImageData = await getFileBinaryData(formData.profileImage);
      console.log('Blob Data:', binaryImageData);

      // Append the file with its field name and binary data
      formDataForBackend.append('profileImage', binaryImageData, formData.profileImage.name, {
        contentDisposition: `form-data; name="profileImage"; filename="${formData.profileImage.name}"`,
      });

      // Log the FormData object to check if binary data is included
      for (var pair of formDataForBackend.entries()) {
        console.log(pair[0] + ', ' + pair[1]);
      }

      // Send POST request to Logic App endpoint using Axios
      const response = await axios.post(
        'https://prod-05.uksouth.logic.azure.com:443/workflows/5e4c364ae4644ee49c285f8aa5a819c5/triggers/manual/paths/invoke?api-version=2016-10-01&sp=%2Ftriggers%2Fmanual%2Frun&sv=1.0&sig=rhsJCcaNO7LNYR1kgYUSbgop1XpTbfbAG3hpgR0azvQ',
        formDataForBackend,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        }
      );

      const result = response.data;

      // Create an object with the expected structure
      const formattedResult = {
        CreatedAt: result.CreatedAt,
        email: result.email,
        filelocation: result.filelocation,
        filename: result.filename,
        filepath: result.filepath,
        id: result.id,
        password: result.password,
        username: result.username,
      };

      console.log('Registration successful:', formattedResult);

      // Optionally, you can redirect the user to another page after successful registration
      // history.push('/login');
    } catch (error) {
      console.error('Registration error:', error.message);
    }
  };

  const getFileBinaryData = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onloadend = () => {
        resolve(new Blob([reader.result], { type: file.type }));
      };
      reader.onerror = reject;
      reader.readAsArrayBuffer(file);
    });
  };

  const handleFileChange = (e) => {
    // Update profile image file in state
    console.log('Selected File:', e.target.files[0]);

    setFormData({ ...formData, profileImage: e.target.files[0] });
  };

  useEffect(() => {
    console.log('Register component mounted!');
  }, []);

  return (
    <div>
      <h2>Register</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <label>
          Username:
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={(e) => setFormData({ ...formData, username: e.target.value })}
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          />
        </label>
        <label>
          Password:
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={(e) => setFormData({ ...formData, password: e.target.value })}
          />
        </label>
        <label>
          Profile Image:
          <input type="file" name="profileImage" onChange={handleFileChange} />
        </label>
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
