import React, { useState, useEffect } from 'react';
import { register } from '../api/auth';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    profileImage: null, // New state for profile image
  });

  const handleFileChange = (e) => {
    const file = e.target.files[0];

    setFormData({
      ...formData,
      profileImage: file,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formDataWithImage = new FormData();

      formDataWithImage.append('username', formData.username);
      formDataWithImage.append('email', formData.email);
      formDataWithImage.append('password', formData.password);

      // Append the profile image file to the form data
      if (formData.profileImage) {
        formDataWithImage.append('profileImage', formData.profileImage);
      }

      const response = await register(formDataWithImage);

      console.log('Registration successful:', response);
      // Optionally, you can redirect the user to another page after successful registration
      // history.push('/login');
    } catch (error) {
      console.error('Registration error:', error.message);
    }
  };

  useEffect(() => {
    console.log('Register component mounted!');
  }, []);

  return (
    <div>
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Username:
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={(e) => setFormData({ ...formData, username: e.target.value })}
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          />
        </label>
        <label>
          Password:
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={(e) => setFormData({ ...formData, password: e.target.value })}
          />
        </label>
        {/* Input for profile image */}
        <label>
          Profile Image:
          <input type="file" accept="image/*" onChange={handleFileChange} />
        </label>
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
