import React, { useState } from 'react';
import axios from 'axios';

const NewJobListing = () => {
  const [title, setTitle] = useState('');
  const [company, setCompany] = useState('');
  const [description, setDescription] = useState('');

  const handlePostJobListing = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/job-listings', {
        Title: title,
        Company: company,
        Description: description,
      });

      console.log('Job Listing posted successfully:', response.data);
      // You can add additional logic or redirect the user after posting the job listing
    } catch (error) {
      console.error('Error posting job listing:', error.response.data);
      // Handle errors or display a user-friendly message
    }
  };

  return (
    <div>
      <h1>Welcome to Job Posting Page</h1>
      <form>
        <div>
          <label htmlFor="title">Job Title:</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="company">Company:</label>
          <input
            type="text"
            id="company"
            value={company}
            onChange={(e) => setCompany(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="description">Job Description:</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <button type="button" onClick={handlePostJobListing}>
          Post Job Listing
        </button>
      </form>
    </div>
  );
};

export default NewJobListing;
