// Import necessary libraries and components
import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import { useAuth } from '../hooks/useAuth';
import { v4 as uuidv4 } from 'uuid';  // Import uuidv4 from the 'uuid' library

// MyResume component
const MyResume = () => {
  // Use the useAuth hook to get the user object
  const { user } = useAuth();
  const [resume, setResume] = useState(null);
  const [video, setVideo] = useState(null);
  const [audio, setAudio] = useState(null);

  // Callback to handle when files are dropped
  const onDrop = useCallback((files) => {
    // Generate a UUID for the entire upload session
    const resumeID = uuidv4();

    // Handle the uploaded files
    console.log('Accepted Files:', files);

    // Group files by type
    const groupedFiles = files.reduce((acc, file) => {
      const fileType = getFileType(file);
      return {
        ...acc,
        [fileType]: {
          content: file,
          path: file.name,
        },
      };
    }, {});
    

    // Set state based on file type
    if (groupedFiles.pdf) {
      setResume(groupedFiles.pdf);
    }
    if (groupedFiles.mp4 || groupedFiles.mov) {
      setVideo(groupedFiles.mp4 || groupedFiles.mov);
    }
    if (groupedFiles.mp3 || groupedFiles.wav) {
      setAudio(groupedFiles.mp3 || groupedFiles.wav);
    }

    // Use the user ID obtained from the useAuth hook
    const userId = user ? user.id : null;

    // Make a POST request to the Azure Functions endpoint if user ID is available
    if (userId) {
      // Pass the generated UUID to the function
      uploadFiles(userId,resumeID, groupedFiles);
    }
  }, [user]);

  // Function to get file type based on extension
  const getFileType = (file) => {
    const extensions = {
      pdf: ['pdf'],
      video: ['mp4', 'mov'],
      audio: ['mp3', 'wav'],
    };

    const extension = file.name.split('.').pop();
    for (const type in extensions) {
      if (extensions[type].includes(extension)) {
        return type;
      }
    }
    return 'unknown';
  };

  // Function to make a POST request to the Azure Functions endpoint
  const uploadFiles = async (userId, resumeID, files) => {
    try {
      const response = await axios.post(
        'https://functionappuserresumes.azurewebsites.net/api/HttpTrigger1?code=4yHlqEo_Erqm96LWxMTJCxwYPtIfAwSkT2DWm9PY6trBAzFuf6rb3Q==',
        { userId, resumeID, files }
      );

      console.log('Upload successful:', response.data);
    } catch (error) {
      console.error('Error uploading files:', error.message);
    }
  };

  // Initialize the dropzones
  const { getRootProps: resumeRootProps, getInputProps: resumeInputProps, isDragActive: isResumeDragActive } = useDropzone({ onDrop });
  const { getRootProps: videoRootProps, getInputProps: videoInputProps, isDragActive: isVideoDragActive } = useDropzone({ onDrop });
  const { getRootProps: audioRootProps, getInputProps: audioInputProps, isDragActive: isAudioDragActive } = useDropzone({ onDrop });

  // Styling for the dropzone area
  const dropzoneStyle = {
    border: '2px dashed #cccccc',
    borderRadius: '4px',
    padding: '20px',
    textAlign: 'center',
    cursor: 'pointer',
    margin: '20px 0',
  };

  // Log when the component renders
  console.log('MyResume Component Rendered');

  return (
    <div>
      <h1>Welcome to Media Upload Page</h1>

      {/* Resume Dropzone area */}
      <div>
        <h2>Upload Your Resume (PDF)</h2>
        <div {...resumeRootProps()} style={dropzoneStyle}>
          <input {...resumeInputProps()} />
          {isResumeDragActive ? (
            <p>Drop the resume here ...</p>
          ) : (
            <p>Drag 'n' drop your resume here, or click to select a file</p>
          )}
        </div>
      </div>
      {/* Display the accepted resume */}
      {resume && (
        <div>
          <h3>Accepted Resume:</h3>
          <p>{resume.name}</p>
        </div>
      )}

      {/* Video Dropzone area */}
      <div>
        <h2>Upload Your Video (MP4 or MOV)</h2>
        <div {...videoRootProps()} style={dropzoneStyle}>
          <input {...videoInputProps()} />
          {isVideoDragActive ? (
            <p>Drop the video here ...</p>
          ) : (
            <p>Drag 'n' drop your video here, or click to select a file</p>
          )}
        </div>
      </div>
      {/* Display the accepted video */}
      {video && (
        <div>
          <h3>Accepted Video:</h3>
          <p>{video.name}</p>
        </div>
      )}

      {/* Audio Dropzone area */}
      <div>
        <h2>Upload Your Audio (MP3 or WAV)</h2>
        <div {...audioRootProps()} style={dropzoneStyle}>
          <input {...audioInputProps()} />
          {isAudioDragActive ? (
            <p>Drop the audio here ...</p>
          ) : (
            <p>Drag 'n' drop your audio here, or click to select a file</p>
          )}
        </div>
      </div>
      {/* Display the accepted audio */}
      {audio && (
        <div>
          <h3>Accepted Audio:</h3>
          <p>{audio.name}</p>
        </div>
      )}
    </div>
  );
};

export default MyResume;
