// UserProfile.jsx

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../hooks/useAuth';

const LogicAppAPI =
  'https://prod-04.uksouth.logic.azure.com:443/workflows/eee57e98e2974fe78e4576d5b2662762/triggers/manual/paths/invoke?api-version=2016-10-01&sp=%2Ftriggers%2Fmanual%2Frun&sv=1.0&sig=nw56HtR2sYXhX_wdTuaH8gcnmBkMOYRyWF6bZQu_9_s';

const base64Decode = (base64String) => {
  const binaryString = window.atob(base64String);
  const bytes = new Uint8Array(binaryString.length);
  for (let i = 0; i < binaryString.length; i++) {
    bytes[i] = binaryString.charCodeAt(i);
  }
  const blob = new Blob([bytes], { type: 'image/jpeg' });
  return URL.createObjectURL(blob);
};

const UserProfile = () => {
  const { user } = useAuth();
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    if (user) {
      fetchUserData(user.id);
    }
  }, [user]);

  const fetchUserData = async (userId) => {
    try {
      const response = await axios.get(`${LogicAppAPI}&userId=${userId}`);

      if (response.data && response.data.value) {
        const userDataArray = response.data.value;

        if (userDataArray.length > 0) {
          // Take the first item in the array as the user data
          const userData = userDataArray[0];
          setUserData(userData);
        } else {
          console.error('Error: Empty user data array');
        }
      } else {
        console.error('Error: Invalid response format');
      }
    } catch (error) {
      console.error('Error fetching user data:', error.message);
    }
  };

  return (
    <div>
      <h1>User Profile</h1>
      {userData && (
        <div>
          <p>Email: {userData.email}</p>
          <p>Username: {userData.username}</p>
          {/* Display user's profile photo */}
          {userData.filelocation && (
            <img
              src={base64Decode(userData.filelocation)}
              alt="Profile Photo"
              style={{ width: '150px', height: '150px', borderRadius: '50%' }}
            />
          )}
        </div>
      )}
    </div>
  );
};

export default UserProfile;
