// src/Pages/Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [loginError, setLoginError] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Simulating authentication logic with hardcoded credentials
    const formData = new FormData(e.target);
    if (formData.get('username') === 'username' && formData.get('password') === 'password') {
      setLoginError(false);
      navigate('/Test'); // Navigate to the Test page after successful login
    } else {
      setLoginError(true);
    }
  };

  return (
    <div className="login">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Username:
          <input type="text" name="username" />
        </label>
        <br />
        <label>
          Password:
          <input type="password" name="password" />
        </label>
        <br />
        <button type="submit">Login</button>
      </form>
      {loginError && <p>Incorrect username or password. Please try again.</p>}
    </div>
  );
};

export default Login;
