import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../hooks/useAuth';

const Home = () => {
  const [allJobListings, setAllJobListings] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);
  const { user } = useAuth();

  useEffect(() => {
    const fetchAllJobListings = async () => {
      try {
        // Fetch all job listings
        const response = await axios.get('http://127.0.0.1:5001/joblistings');
        setAllJobListings(response.data);
        console.log("r is:", response.data);
      } catch (error) {
        console.error('Error fetching all job listings:', error.message);
        // Handle errors or display a user-friendly message
      }
    };

    fetchAllJobListings();
  }, []);

  const handleJobClick = (job) => {
    setSelectedJob(job === selectedJob ? null : job);
  };

  const handleApply = async (job) => {
    try {
      const cosmosDBUserId = user.id;
      const jobId = job.ID;

      if (cosmosDBUserId && jobId) {
        // Make a POST request to the jobapplications endpoint
        const response = await axios.post('http://127.0.0.1:5001/jobapplications', {
          JobID: jobId,
          CosmosDBUserID: cosmosDBUserId,
        });

        console.log('Application successful:', response.data);
        // You can handle the response as needed
      } else {
        console.error('Cosmos DB user ID or Job ID not found.');
      }
    } catch (error) {
      console.error('Error applying to job:', error.message);
      // Handle errors or display a user-friendly message
    }
  };

  const handleLike = (job) => {
    // Implement logic to handle liking a job
    console.log('Liked job:', job.ID);
  };

  const handleDislike = (job) => {
    // Implement logic to handle disliking a job
    console.log('Disliked job:', job.ID);
  };

  return (
    <div>
      <h1>All Job Listings:</h1>
      <style>
        {`
          .job-listings {
            display: flex;
            flex-wrap: wrap;
          }

          .job-box {
            border: 1px solid #ccc;
            margin: 10px;
            padding: 15px;
            cursor: pointer;
            width: 200px;
          }

          .job-box.selected {
            border-color: #007bff;
          }

          .job-title {
            font-size: 1.2rem;
            margin-bottom: 10px;
          }

          .job-company {
            color: #555;
            margin-bottom: 8px;
          }

          .job-description-preview {
            margin-bottom: 15px;
          }

          .job-details {
            margin-top: 10px;
          }

          .job-likes-dislikes {
            display: flex;
            justify-content: space-between;
            cursor: pointer;
          }

          .job-likes, .job-dislikes {
            font-size: 0.9rem;
          }
        `}
      </style>
      <div className="job-listings">
        {allJobListings.length > 0 ? (
          allJobListings.map((job) => (
            <div key={job.id} className={`job-box ${selectedJob === job ? 'selected' : ''}`} onClick={() => handleJobClick(job)}>
              <h2 className="job-title">{job.Title}</h2>
              <p className="job-company">{job.Company}</p>
              <p className="see-more">View full Job Description...</p>
              {selectedJob === job && (
                <div className="job-details">
                  <p className="job-description">{job.Description}</p>
                </div>
              )}
              <div className="job-likes-dislikes">
                <div className="job-likes" onClick={() => handleLike(job)}>
                  👍 {job.LikesCount}
                </div>
                <div className="job-dislikes" onClick={() => handleDislike(job)}>
                  👎 {job.DislikesCount}
                </div>
              </div>
              <button className="apply-button" onClick={() => handleApply(job)}>
                Apply
              </button>
            </div>
          ))
        ) : (
          <p>No job listings found.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
