import React, { useState, useEffect } from 'react';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    profileImage: null,
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formDataForBackend = new FormData();
      formDataForBackend.append('username', formData.username);
      formDataForBackend.append('email', formData.email);
      formDataForBackend.append('password', formData.password);

      // Append the image file with a specific key ('profileImage' in this case)
      formDataForBackend.append('profileImage', formData.profileImage, formData.profileImage.name);

      const response = await fetch(
        'https://prod-05.uksouth.logic.azure.com:443/workflows/5e4c364ae4644ee49c285f8aa5a819c5/triggers/manual/paths/invoke?api-version=2016-10-01&sp=%2Ftriggers%2Fmanual%2Frun&sv=1.0&sig=rhsJCcaNO7LNYR1kgYUSbgop1XpTbfbAG3hpgR0azvQ',
        {
          method: 'POST',
          body: formDataForBackend,
        }
      );

      const result = await response.json();

      // Access nested properties
      const formattedResult = {
        CreatedAt: result.CreatedAt,
        email: result.email,
        filelocation: result.filelocation,
        filename: 'profile-photo.png', // This should match the expected filename
        filepath: result.filepath,
        id: result.id,
        password: result.password,
        username: result.username,
      };

      console.log('Registration successful:', formattedResult);
    } catch (error) {
      console.error('Registration error:', error.message);
    }
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFormData({ ...formData, profileImage: selectedFile });
  
    // Log file details to ensure it's selected correctly
    console.log('File details:', selectedFile);
  };
  

  useEffect(() => {
    console.log('Register component mounted!');
  }, []);

  return (
    <div>
      <h2>Register</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <label>
          Username:
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={(e) => setFormData({ ...formData, username: e.target.value })}
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          />
        </label>
        <label>
          Password:
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={(e) => setFormData({ ...formData, password: e.target.value })}
          />
        </label>
        <label>
          Profile Image:
          <input type="file" name="profileImage" onChange={handleFileChange} />
        </label>
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
