import React, { useState, useEffect } from 'react';
import { FaSpinner } from 'react-icons/fa';

const DisplayResume = () => {
  const userId = 'JTJmdXNlcnByb2ZpbGVzY29udGFpbmVyJTJmNjM4MzcyNTMxNDIzMjQ1OTY4';
  const functionUri = 'https://functionappuserresumes.azurewebsites.net/api/HttpTriggerGetResume?code=3xBINMjx9M_ybw4gII-HTDCL5ejk46kuzkfwebsa1gMpAzFu5xSR0w==';

  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFiles = async () => {
      try {
        const response = await fetch(`${functionUri}&userId=${userId}`);
        if (!response.ok) {
          throw new Error(`Error fetching files: ${response.statusText}`);
        }

        const data = await response.json();
        console.log('Data received:', data); // Log the received data
        setFiles(data.files);
      } catch (error) {
        console.error('Error fetching files:', error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchFiles();
  }, [userId, functionUri]);

  // Helper function to determine file type and content type based on extension
  function getFileType(fileName) {
    const extension = fileName.split('.').pop().toLowerCase();
    console.log(extension);
    switch (extension) {
      case 'pdf':
        return 'application/pdf';
      case 'audio':
        return 'audio/mov';
      case 'video':
        return 'video/mp4';
      default:
        return 'application/octet-stream';
    }
  }

  return (
    <div style={{ maxWidth: '800px', margin: '20px auto 0', position: 'relative' }}>
      {loading && (
        <div style={{ textAlign: 'center', marginBottom: '20px' }}>
          <FaSpinner size={40} className="loading-icon" />
        </div>
      )}

      {files.length === 0 && !loading && <p>No files available.</p>}

      {files.map((file, index) => (
        <div key={index} style={{ border: '2px solid #ccc', borderRadius: '8px', marginBottom: '20px', padding: '20px' }}>
          <h2 style={{ marginBottom: '10px' }}>{getFileType(file.fileName)}</h2>

          {file.fileType === 'audio/mov' ? (
            <audio controls>
              <source src={`data:${file.contentType};base64,${file.content}`} type={file.contentType} />
              Your browser does not support the audio element.
            </audio>
          ) : file.fileType === 'video/mp4' ? (
            <video controls width="100%" height="500px">
              <source src={`data:${file.contentType};base64,${file.content}`} type={file.contentType} />
              Your browser does not support the video element.
            </video>
          ) : (
            <p>Unsupported file type</p>
          )}
        </div>
      ))}
    </div>
  );
};

export default DisplayResume;
