// Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Login = () => {
  const [loginError, setLoginError] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const username = formData.get('username');
    const password = formData.get('password');

    // In a real-world scenario, you would send a request to the server to authenticate the user.
    // For simplicity, we'll check a hardcoded username and password.
    if (username === 'username' && password === 'password') {
      setLoginError(false);
      login(username, password);
      navigate('/Test');
    } else {
      setLoginError(true);
    }
  };

  return (
    <div className="login">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Username:
          <input type="text" name="username" />
        </label>
        <br />
        <label>
          Password:
          <input type="password" name="password" />
        </label>
        <br />
        <button type="submit">Login</button>
      </form>
      {loginError && <p>Incorrect username or password. Please try again.</p>}
    </div>
  );
};

export default Login;
