import React, { useState, useEffect } from 'react';

const DisplayResume = () => {
  const userId = 'JTJmdXNlcnByb2ZpbGVzY29udGFpbmVyJTJmNjM4MzcxNjc0MzA1ODY1NDUx'; // Replace with the actual user ID
  const functionUri = 'https://functionappuserresumes.azurewebsites.net/api/HttpTriggerGetResume?code=3xBINMjx9M_ybw4gII-HTDCL5ejk46kuzkfwebsa1gMpAzFu5xSR0w==';

  const [files, setFiles] = useState([]);

  useEffect(() => {
    const fetchFiles = async () => {
      try {
        const response = await fetch(`${functionUri}&userId=${userId}`);
        if (!response.ok) {
          throw new Error(`Error fetching files: ${response.statusText}`);
        }

        const data = await response.json();
        setFiles(data.files);
      } catch (error) {
        console.error('Error:', error.message);
      }
    };

    fetchFiles();
  }, [userId, functionUri]);

  return (
    <div style={{ maxWidth: '800px', margin: '20px auto 0' }}>
      {files.length === 0 ? (
        <p>Loading...</p>
      ) : (
        files.map((file, index) => (
          <div key={index} style={{ border: '2px solid #ccc', borderRadius: '8px', marginBottom: '20px', padding: '20px' }}>
            <h2 style={{ marginBottom: '10px' }}>{getFileTitle(file.fileName)}</h2>
            {file.contentType === 'audio/mp3' ? (
              <>
                <div style={{ textAlign: 'center', fontSize: '2em', marginBottom: '10px' }}>
                  ðŸŽµ {/* Musical note emoji or replace with your preferred music icon */}
                </div>
                <audio controls style={{ width: '100%', maxWidth: '400px', margin: '0 auto' }}>
                  <source src={`data:${file.contentType};base64,${file.content}`} type="audio/mp3" />
                  Your browser does not support the audio tag.
                </audio>
              </>
            ) : (
              <iframe
                title={`File-${index}`}
                src={`data:${file.contentType};base64,${file.content}`}
                width="100%"
                height="500px"
                style={{ border: 'none' }}
                allow="autoplay"
              />
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default DisplayResume;

// Helper function to determine the title based on file name
function getFileTitle(fileName) {
  const extension = fileName.split('.').pop().toLowerCase();
  console.log('Extension:', extension);
  switch (extension) {
    case 'pdf':
      return 'My Resume (PDF)';
    case 'video':
      return 'My Personal Video (MP4)';
    case 'audio':
      return 'My Aspirations (MP3)';
    default:
      return 'File'; // Default title if type is unknown
  }
}
