// Modal.js

import React from 'react';

const Modal = ({ isOpen, onClose, title, likes, dislikes, userApplications }) => {
  return (
    <>
      {isOpen && (
        <div className="modal" onClick={onClose}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h2 className="modal-title">{title}</h2>
            {likes.length > 0 && (
              <div>
                <h3>Likes</h3>
                <ul className="user-list">
                  {likes.map((username) => (
                    <li key={username} className="user-item">
                      {username} <span className="thumbs-up">üëç</span>
                    </li>
                  ))}
                </ul>
              </div>
            )}
            {dislikes.length > 0 && (
              <div>
                <h3>Dislikes</h3>
                <ul className="user-list">
                  {dislikes.map((username) => (
                    <li key={username} className="user-item">
                      {username} <span className="thumbs-down">üëé</span>
                    </li>
                  ))}
                </ul>
              </div>
            )}
            {userApplications.length > 0 && (
              <div>
                <h3>Applicants</h3>
                <ul className="user-list">
                  {userApplications.map((applicant) => (
                    <li key={applicant.CosmosDBUserID} className="user-item">
                      <div>
                        <strong>{applicant.username}</strong>
                      </div>
                      <div>{applicant.email}</div>
                    </li>
                  ))}
                </ul>
              </div>
            )}
            <button onClick={onClose}>Close</button>
          </div>
        </div>
      )}
    </>
  );
};

export default Modal;
