import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Home = () => {
  const [allJobListings, setAllJobListings] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);
  const [expandedJobs, setExpandedJobs] = useState([]);

  useEffect(() => {
    const fetchAllJobListings = async () => {
      try {
        // Fetch all job listings
        const response = await axios.get('http://127.0.0.1:5001/joblistings');
        console.log("r is:",response)
        setAllJobListings(response.data);
      } catch (error) {
        console.error('Error fetching all job listings:', error.message);
        // Handle errors or display a user-friendly message
      }
    };

    fetchAllJobListings();
  }, []);

  const handleJobClick = (job) => {
    setSelectedJob(job === selectedJob ? null : job);
    setExpandedJobs((prevExpandedJobs) => {
      if (prevExpandedJobs.includes(job.id)) {
        // Job is expanded, so collapse it
        return prevExpandedJobs.filter((id) => id !== job.id);
      } else {
        // Job is not expanded, so expand it
        return [...prevExpandedJobs, job.id];
      }
    });
  };

  const handleApply = (job) => {
    // Implement the logic to handle job application
    console.log(`Applying to job: ${job.id}`);
    // You can send a request to your backend to handle the application
  };

  return (
    <div>
      <h1>All Job Listings:</h1>
      <style>
        {`
          .job-listings {
            display: flex;
            flex-wrap: wrap;
          }

          .job-box {
            border: 1px solid #ccc;
            margin: 10px;
            padding: 15px;
            cursor: pointer;
            width: 200px;
          }

          .job-box.selected {
            border-color: #007bff;
          }

          .job-title {
            font-size: 1.2rem;
            margin-bottom: 10px;
          }

          .job-company {
            color: #555;
            margin-bottom: 8px;
          }

          .job-description-preview {
            margin-bottom: 15px;
          }

          .job-details {
            margin-top: 10px;
          }

          .job-likes-dislikes {
            display: flex;
            justify-content: space-between;
          }

          .job-likes, .job-dislikes {
            font-size: 0.9rem;
          }
        `}
      </style>
      <div className="job-listings">
        {allJobListings.length > 0 ? (
          allJobListings.map((job) => (
            <div key={job.id} className={`job-box ${selectedJob === job ? 'selected' : ''}`} onClick={() => handleJobClick(job)}>
              <h2 className="job-title">{job.Title}</h2>
              <p className="job-company">{job.Company}</p>
              <p className="see-more" style={{ display: expandedJobs.includes(job.id) ? 'none' : 'block' }}>
                View full Job Description...
              </p>
              {selectedJob === job && (
                <div className="job-details">
                  <p className="job-description">{job.Description}</p>
                </div>
              )}
              <div className="job-likes-dislikes">
                <div className="job-likes">
                  👍 {job.LikesCount}
                </div>
                <div className="job-dislikes">
                  👎 {job.DislikesCount}
                </div>
              </div>
              <button className="apply-button" onClick={() => handleApply(job)}>
                Apply
              </button>
            </div>
          ))
        ) : (
          <p>No job listings found.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
