import React, { useState } from 'react';
import axios from 'axios';

const RegisterForm = () => {
  const [formData, setFormData] = useState({
    id: 12,
    CreatedAt: '2023-11-30T00:00:00', // Replace with the actual date
    email: 'user@email.com',
    filelocation: 'local',
    filename: 'profile-photo.png',
    filepath: '/path/to/file',
    password: 'password123',
    username: 'john_doe',
  });

  const [file, setFile] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    for (const key in formData) {
      data.append(key, formData[key]);
    }

    if (file) {
      data.append('file', file);
    }

    try {
      const response = await axios.post(
        'https://prod-05.uksouth.logic.azure.com:443/workflows/5e4c364ae4644ee49c285f8aa5a819c5/triggers/manual/paths/invoke?api-version=2016-10-01&sp=%2Ftriggers%2Fmanual%2Frun&sv=1.0&sig=rhsJCcaNO7LNYR1kgYUSbgop1XpTbfbAG3hpgR0azvQ',
        data,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        }
      );

      console.log('Successful Response:', response);
      // Handle success or update UI accordingly
    } catch (error) {
      console.error('Error sending data:', error);

      if (error.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        console.error('Response Data:', error.response.data);
        console.error('Response Status:', error.response.status);
        console.error('Response Headers:', error.response.headers);
      } else if (error.request) {
        // The request was made but no response was received
        console.error('No Response Received:', error.request);
      } else {
        // Something happened in setting up the request that triggered an Error
        console.error('Error Setting Up Request:', error.message);
      }

      // Handle error or update UI accordingly
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);

    const newFormData = { ...formData, file: selectedFile };
    setFormData(newFormData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        ID:
        <input type="number" name="id" value={formData.id} onChange={handleChange} />
      </label>
      <label>
        Email:
        <input type="email" name="email" value={formData.email} onChange={handleChange} />
      </label>
      {/* Add similar input fields for other form data */}
      <label>
        Profile Photo:
        <input type="file" name="file" onChange={handleFileChange} accept="image/*" />
      </label>
      <button type="submit">Register</button>
    </form>
  );
};

export default RegisterForm;
