import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../hooks/useAuth'; // Import your custom authentication hook

const LogicAppAPI =
  'https://prod-04.uksouth.logic.azure.com:443/workflows/eee57e98e2974fe78e4576d5b2662762/triggers/manual/paths/invoke?api-version=2016-10-01&sp=%2Ftriggers%2Fmanual%2Frun&sv=1.0&sig=nw56HtR2sYXhX_wdTuaH8gcnmBkMOYRyWF6bZQu_9_s';

const EditDetails = () => {
  const { user } = useAuth(); // Use your custom authentication hook to get the user object
  const [userData, setUserData] = useState({});
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [deleteConfirmation, setDeleteConfirmation] = useState('');

  useEffect(() => {
    if (user) {
      fetchUserData(user);
    }
  }, [user]);

  const fetchUserData = async (user) => {
    try {
      const response = await axios.get(`${LogicAppAPI}&userId=${user.id}`);
      setUserData(response.data);
      setUsername(response.data.username);
      setEmail(response.data.email);
    } catch (error) {
      console.error('Error fetching user data:', error.message);
    }
  };

  const handleUpdateDetails = async () => {
    try {
      const updatedData = {
        username: username,
        email: email,
        password: password,
      };

      // Make the Logic App call to update user details
      await axios.put(`${LogicAppAPI}&userId=${userData.id}`, updatedData);

      console.log('User details updated successfully');
      // You can add additional logic or redirect the user after updating details
    } catch (error) {
      console.error('Error updating user details:', error.message);
      // Handle errors or display a user-friendly message
    }
  };

  const handleDeleteAccount = async () => {
    if (deleteConfirmation === 'DELETE') {
      try {
        // Make the Logic App call to delete user account
        await axios.delete(`${LogicAppAPI}&userId=${userData.id}`);
        console.log('User account deleted successfully');
        // You can add additional logic or redirect the user after deleting the account
      } catch (error) {
        console.error('Error deleting user account:', error.message);
        // Handle errors or display a user-friendly message
      }
    } else {
      console.error('Delete confirmation does not match. Account not deleted.');
      // Display a message or alert to the user indicating a mismatch in confirmation
    }
  };

  const containerStyle = {
    maxWidth: '400px',
    height: '500px',
    margin: '0 auto',
    padding: '20px',
    border: '1px solid #ccc',
    borderRadius: '8px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
  };

  const labelStyle = {
    fontWeight: 'bold',
    color: '#555',
    marginBottom: '8px',
    display: 'block',
  };

  const inputStyle = {
    width: '100%',
    padding: '10px',
    boxSizing: 'border-box',
    marginBottom: '10px', // Add margin bottom for spacing
  };

  const buttonContainerStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    marginTop: '10px',
  };

  const buttonStyle = {
    flex: 1,
    backgroundColor: '#4caf50',
    color: 'white',
    padding: '10px',
    border: 'none',
    cursor: 'pointer',
    marginRight: '10px',
  };

  const deleteButtonStyle = {
    flex: 1,
    backgroundColor: '#ff0000',
    color: 'white',
    padding: '10px',
    border: 'none',
    cursor: 'pointer',
  };

  return (
    <div style={containerStyle}>
      <h1 style={{ textAlign: 'center', color: '#333' }}>Edit Your Details</h1>
      {userData && (
        <form>
          <label htmlFor="username" style={labelStyle}>
            Username:
          </label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            style={inputStyle}
          />

          <label htmlFor="email" style={labelStyle}>
            Email:
          </label>
          <input
            type="text"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={inputStyle}
          />

          <label htmlFor="password" style={labelStyle}>
            New Password:
          </label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={inputStyle}
          />

          {/* Display user's profile photo */}
          <div style={{ textAlign: 'center', marginBottom: '20px' }}>
            {userData.filepath && (
              <img
                src={userData.filepath}
                alt="Profile Photo"
                style={{ width: '150px', height: '150px', borderRadius: '50%' }}
              />
            )}
          </div>

          <div style={buttonContainerStyle}>
            <button type="button" onClick={handleUpdateDetails} style={buttonStyle}>
              Update Details
            </button>

            <div style={{ flex: 1, marginLeft: '10px' }}>
              <label htmlFor="deleteConfirmation" style={labelStyle}>
                Type 'DELETE' to confirm:
              </label>
              <input
                type="text"
                id="deleteConfirmation"
                value={deleteConfirmation}
                onChange={(e) => setDeleteConfirmation(e.target.value)}
                style={inputStyle}
              />
              <button type="button" onClick={handleDeleteAccount} style={deleteButtonStyle}>
                Delete Account
              </button>
            </div>
          </div>
        </form>
      )}
    </div>
  );
};

export default EditDetails;
