import React, { useState, useEffect } from 'react';

const Register = () => {
  const [formData, setFormData] = useState({
    username: 'john_doe',
    email: 'user@example.com',
    password: 'password123',
    profileImage: null, // Set the file later during testing
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const base64Image = await getBase64(formData.profileImage);

      // Log the base64Image for inspection
      console.log('Base64 Image:', base64Image);

      // Simulate a successful response for testing
      const simulatedResponse = {
        CreatedAt: '2023-11-30T17:26:45.200Z',
        email: 'user@example.com',
        filelocation: 'JTJmdXNlcnByb2ZpbGVzY29udGFpbmVyJTJmNjM4MzY5NjIwMDUwODE2MjA1',
        filename: 'profile-photo.png',
        filepath: '/userprofilescontainer/638369620050816205',
        id: 'JTJmdXNlcnByb2ZpbGVzY29udGFpbmVyJTJmNjM4MzY5NjIwMDUwODE2MjA1',
        password: 'password123',
        username: 'john_doe',
      };

      const formattedResult = {
        CreatedAt: simulatedResponse.CreatedAt,
        email: simulatedResponse.email,
        filelocation: simulatedResponse.filelocation,
        filename: formData.profileImage.name,
        filepath: simulatedResponse.filepath,
        id: simulatedResponse.id,
        password: simulatedResponse.password,
        username: formData.username, // Include other input fields
      };

      console.log('Registration successful:', formattedResult);
    } catch (error) {
      console.error('Registration error:', error.message);
    }
  };

  const handleFileChange = (e) => {
    // For testing purposes, you can use the same file
    setFormData({ ...formData, profileImage: e.target.files[0] });
  };

  useEffect(() => {
    console.log('Register component mounted!');
  }, []);

  // Function to convert file to base64
  const getBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });
  };

  return (
    <div>
      <h2>Register</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <label>
          Username:
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={(e) => setFormData({ ...formData, username: e.target.value })}
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          />
        </label>
        <label>
          Password:
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={(e) => setFormData({ ...formData, password: e.target.value })}
          />
        </label>
        {/* Other input fields go here */}
        <label>
          Profile Image:
          <input type="file" name="profileImage" onChange={handleFileChange} />
        </label>
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
