import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../hooks/useAuth';

const LogicAppAPI =
  'https://prod-04.uksouth.logic.azure.com:443/workflows/eee57e98e2974fe78e4576d5b2662762/triggers/manual/paths/invoke?api-version=2016-10-01&sp=%2Ftriggers%2Fmanual%2Frun&sv=1.0&sig=nw56HtR2sYXhX_wdTuaH8gcnmBkMOYRyWF6bZQu_9_s';

const UserProfile = () => {
  const { user } = useAuth();
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    if (user) {
      fetchUserData(user.id);
    }
  }, [user]);

  const fetchUserData = async (userId) => {
    try {
      const response = await axios.get(`${LogicAppAPI}&userId=${userId}`);
      setUserData(response.data[0]);
    } catch (error) {
      console.error('Error fetching user data:', error.message);
    }
  };

  return (
    <div>
      <h1>User Profile</h1>
      {userData && (
        <div>
          <p>Email: {userData.email}</p>
          <p>Username: {userData.username}</p>
          {/* Display user's profile photo */}
          {userData.filepath && (
            <img
              src={userData.filepath}
              alt="Profile Photo"
              style={{ width: '150px', height: '150px', borderRadius: '50%' }}
            />
          )}
        </div>
      )}
    </div>
  );
};

export default UserProfile;
