import React, { useState, useEffect } from 'react';
import { FaSpinner } from 'react-icons/fa';

const DisplayResume = () => {
  const userId = 'JTJmdXNlcnByb2ZpbGVzY29udGFpbmVyJTJmNjM4MzcyNTMxNDIzMjQ1OTY4';
  const functionUriGetFiles = 'https://functionappuserresumes.azurewebsites.net/api/HttpTriggerGetResume?code=3xBINMjx9M_ybw4gII-HTDCL5ejk46kuzkfwebsa1gMpAzFu5xSR0w==';
  const functionUriGetTranscript = 'https://functionappuserresumes.azurewebsites.net/api/HttpTriggerGetTranscript?code=your-function-code'; // Add the code for this function

  const [files, setFiles] = useState([]);
  const [transcript, setTranscript] = useState('');
  const [loadingFiles, setLoadingFiles] = useState(true);
  const [loadingTranscript, setLoadingTranscript] = useState(true);

  useEffect(() => {
    const fetchFiles = async () => {
      try {
        const response = await fetch(`${functionUriGetFiles}&userId=${userId}`);
        if (!response.ok) {
          throw new Error(`Error fetching files: ${response.statusText}`);
        }

        const data = await response.json();
        setFiles(data.files);
      } catch (error) {
        console.error('Error fetching files:', error.message);
      } finally {
        setLoadingFiles(false);
      }
    };

    const fetchTranscript = async () => {
      try {
        const response = await fetch(`${functionUriGetTranscript}&userId=${userId}`);
        if (!response.ok) {
          throw new Error(`Error fetching transcript: ${response.statusText}`);
        }

        const data = await response.json();
        setTranscript(data.transcript);
      } catch (error) {
        console.error('Error fetching transcript:', error.message);
      } finally {
        setLoadingTranscript(false);
      }
    };

    // Make separate calls for files and transcript
    fetchFiles();
    fetchTranscript();
  }, [userId, functionUriGetFiles, functionUriGetTranscript]);

  // Helper function to determine file type and content type based on extension
  function getFileType(fileName) {
    const extension = fileName.split('.').pop().toLowerCase();
    console.log(extension);
    switch (extension) {
      case 'pdf':
        return 'application/pdf';
      case 'audio':
        return 'audio/mov';
      case 'video':
        return 'video/mp4';
      default:
        return 'application/octet-stream';
    }
  }

  // Separate files into arrays based on file type
  const pdfFiles = files.filter((file) => getFileType(file.fileName) === 'application/pdf');
  const videoFiles = files.filter((file) => getFileType(file.fileName) === 'video/mp4');
  const audioFiles = files.filter((file) => getFileType(file.fileName) === 'audio/mov');

  // Concatenate the arrays to display PDFs first, then videos, and finally audio files
  const orderedFiles = pdfFiles.concat(videoFiles, audioFiles);

  return (
    <div style={{ maxWidth: '800px', margin: '20px auto 0', position: 'relative' }}>
      {loadingFiles && (
        <div style={{ textAlign: 'center', marginBottom: '20px' }}>
          <FaSpinner size={40} className="loading-icon" />
        </div>
      )}

      {orderedFiles.length === 0 && !loadingFiles && !loadingTranscript && <p>No files available.</p>}

      {orderedFiles.map((file, index) => (
        <div key={index} style={{ border: '2px solid #ccc', borderRadius: '8px', marginBottom: '20px', padding: '20px' }}>
          <h2 style={{ marginBottom: '10px' }}>{getFileType(file.fileName)}</h2>

          {file.fileType === 'audio/mov' ? (
            <audio controls>
              <source src={`data:${getFileType(file.fileName)};base64,${file.content}`} type="audio/mpeg" />
              Your browser does not support the audio element.
            </audio>
          ) : (
            <iframe
              title={`File-${index}`}
              src={`data:${getFileType(file.fileName)};base64,${file.content}`}
              width="100%"
              height="500px"
              style={{ border: 'none' }}
              allow="autoplay"
            />
          )}
        </div>
      ))}

      {loadingTranscript && (
        <div style={{ textAlign: 'center', marginTop: '20px' }}>
          <FaSpinner size={40} className="loading-icon" />
          <p>Loading transcript...</p>
        </div>
      )}

      {!loadingTranscript && transcript && (
        <div style={{ border: '2px solid #ccc', borderRadius: '8px', marginTop: '20px', padding: '20px' }}>
          <h2>Transcript</h2>
          <p>{transcript}</p>
        </div>
      )}
    </div>
  );
};

export default DisplayResume;
