import React, { useState, useEffect } from 'react';
import { useAuth } from '../hooks/useAuth';
import axios from 'axios';

const MyJobs = () => {
  const { user, isLoggedIn } = useAuth();
  const [jobListings, setJobListings] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);
  const [showFullDescription, setShowFullDescription] = useState(false);

  const fetchJobListings = async () => {
    try {
      if (!user) {
        console.error('User is null');
        return;
      }

      // Fetch job listings for the logged-in user
      const response = await axios.get(`http://127.0.0.1:5001/joblistings?CosmosDBUserID=${user.id}`);
      const jobListingsWithFeedback = await Promise.all(
        response.data.map(async (job) => {
          // Fetch job feedback for each job listing
          const feedbackResponse = await axios.get(`http://127.0.0.1:5001/jobfeedback?JobID=${job.ID}`);
          const likedUsers = await Promise.all(
            feedbackResponse.data
              .filter((feedback) => feedback.IsLiked)
              .map(async (feedback) => {
                const userResponse = await axios.get(`http://127.0.0.1:5000/api/users/${feedback.CosmosDBUserID}`);
                return userResponse.data.username;
              })
          );

          const dislikedUsers = await Promise.all(
            feedbackResponse.data
              .filter((feedback) => !feedback.IsLiked)
              .map(async (feedback) => {
                const userResponse = await axios.get(`http://127.0.0.1:5000/api/users/${feedback.CosmosDBUserID}`);
                return userResponse.data.username;
              })
          );

          return {
            ...job,
            likedUsers,
            dislikedUsers,
          };
        })
      );

      setJobListings(jobListingsWithFeedback);
    } catch (error) {
      console.error('Error fetching job listings:', error.message);
      // Handle errors or display a user-friendly message
    }
  };

  useEffect(() => {
    if (isLoggedIn) {
      fetchJobListings();
    }
  }, [isLoggedIn, user]);

  const handleJobClick = (job) => {
    if (selectedJob === job) {
      setSelectedJob(null);
      setShowFullDescription(false);
    } else {
      setSelectedJob(job);
      setShowFullDescription(true);
    }
  };

  const handleDeleteJob = async (jobId) => {
    try {
      console.log('job id', jobId);
      await axios.delete(`http://127.0.0.1:5001/joblistings/${jobId}`);
      // After successful deletion, fetch the updated job listings
      fetchJobListings();
    } catch (error) {
      console.error('Error deleting job listing:', error.message);
      // Handle errors or display a user-friendly message
    }
  };

  return (
    <div>
      <h1>You have posted the following job listings:</h1>
      <style>
        {`
          // Add your CSS styles here
          // ...

          .job-listings {
            display: flex;
            flex-wrap: wrap;
          }

          .job-box {
            border: 1px solid #ccc;
            margin: 10px;
            padding: 15px;
            cursor: pointer;
            width: 200px;
          }

          .job-box.selected {
            border-color: #007bff;
          }

          .job-title {
            font-size: 1.2rem;
            margin-bottom: 10px;
          }

          .job-company {
            color: #555;
            margin-bottom: 8px;
          }

          .job-description-preview {
            margin-bottom: 15px;
          }

          .job-details {
            margin-top: 10px;
          }

          .job-likes-dislikes {
            display: flex;
            justify-content: space-between;
          }

          .job-likes,
          .job-dislikes {
            font-size: 0.9rem;
          }

          .see-more {
            cursor: pointer;
            color: #007bff;
            text-decoration: underline;
          }
        `}
      </style>
      {isLoggedIn ? (
        <div>
          {jobListings.length > 0 ? (
            <div className="job-listings">
              {jobListings.map((job) => (
                <div key={job.ID} className={`job-box ${selectedJob === job ? 'selected' : ''}`} onClick={() => handleJobClick(job)}>
                  <h2 className="job-title">{job.Title}</h2>
                  <p className="job-company">{job.Company}</p>
                  {showFullDescription && selectedJob === job ? (
                    <div className="job-details">
                      <p className="job-description">{job.Description}</p>
                      <div>
                        <p>Liked by: {job.likedUsers.join(', ')}</p>
                        <p>Disliked by: {job.dislikedUsers.join(', ')}</p>
                      </div>
                    </div>
                  ) : (
                    <p className="see-more" onClick={() => setShowFullDescription(true)}>
                      View full Job Description...
                    </p>
                  )}
                  <div className="job-likes-dislikes">
                    <div className="job-likes">
                      üëç {job.LikesCount}
                    </div>
                    <div className="job-dislikes">
                      üëé {job.DislikesCount}
                    </div>
                  </div>
                  <button onClick={() => handleDeleteJob(job.ID)}>Delete</button>
                </div>
              ))}
            </div>
          ) : (
            <p>No job listings found.</p>
          )}
        </div>
      ) : (
        <p>Please log in to view your job listings.</p>
      )}
    </div>
  );
};

export default MyJobs;
