// DisplayResume.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DisplayResume = () => {
  const [filesMetadata, setFilesMetadata] = useState([]);

  useEffect(() => {
    const fetchFilesMetadata = async () => {
      const userId = "JTJmdXNlcnByb2ZpbGVzY29udGFpbmVyJTJmNjM4MzcxNjc0MzA1ODY1NDUx";

      try {
        const response = await axios.get(
          `https://functionappuserresumes.azurewebsites.net/api/HttpTriggerGetResume?code=3xBINMjx9M_ybw4gII-HTDCL5ejk46kuzkfwebsa1gMpAzFu5xSR0w==&userId=${userId}`
        );

        if (Array.isArray(response.data.files)) {
          setFilesMetadata(response.data.files);
        } else {
          console.error('Invalid data format received:', response.data);
        }
      } catch (error) {
        console.error('Error fetching files metadata:', error.message);
      }
    };

    fetchFilesMetadata();
  }, []);

  return (
    <div>
      <h1>Display Resumes</h1>
      <ul>
        {filesMetadata.map((file) => (
          <li key={file.fileName}>
            <strong>File Name:</strong> {file.fileName}
            <br />
            <audio controls>
              <source src={`data:audio/mp3;base64,${file.content}`} type="audio/mp3" />
              Your browser does not support the audio element.
            </audio>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default DisplayResume;
