// ../api/jobs.js

import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

export const postJobListing = async (jobData) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/job-listings`, jobData);
    return response.data;
  } catch (error) {
    throw error.response.data;
  }
};
