import React, { useState, useEffect } from 'react';
import { useAuth } from '../hooks/useAuth';
import { updateUserDetails, deleteUser } from '../api/auth';

const EditDetails = () => {
  const { user, isLoggedIn } = useAuth();
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [deleteConfirmation, setDeleteConfirmation] = useState('');

  useEffect(() => {
    if (isLoggedIn && user) {
      setUsername(user.username);
      setEmail(user.email);
    }
  }, [isLoggedIn, user]);

  const handleUpdateDetails = async () => {
    try {
      const updatedData = {
        username: username,
        email: email,
        password: password,
      };

      await updateUserDetails(user.id, updatedData);
      console.log('User details updated successfully');
      // You can add additional logic or redirect the user after updating details
    } catch (error) {
      console.error('Error updating user details:', error.message);
      // Handle errors or display a user-friendly message
    }
  };

  const handleDeleteAccount = async () => {
    if (deleteConfirmation === 'DELETE') {
      try {
        await deleteUser(user.id);
        console.log('User account deleted successfully');
        // You can add additional logic or redirect the user after deleting the account
      } catch (error) {
        console.error('Error deleting user account:', error.message);
        // Handle errors or display a user-friendly message
      }
    } else {
      console.error('Delete confirmation does not match. Account not deleted.');
      // Display a message or alert to the user indicating a mismatch in confirmation
    }
  };

  return (
    <div style={{ maxWidth: '400px', margin: '0 auto', padding: '20px', border: '1px solid #ccc', borderRadius: '8px', boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)' }}>
      <h1 style={{ textAlign: 'center' }}>Welcome to Edit Details Page</h1>
      {isLoggedIn ? (
        <form style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
          <div>
            <label htmlFor="username" style={{ fontWeight: 'bold' }}>Username:</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              style={{ width: '100%', padding: '8px', boxSizing: 'border-box' }}
            />
          </div>
          <div>
            <label htmlFor="email" style={{ fontWeight: 'bold' }}>Email:</label>
            <input
              type="text"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={{ width: '100%', padding: '8px', boxSizing: 'border-box' }}
            />
          </div>
          <div>
            <label htmlFor="password" style={{ fontWeight: 'bold' }}>New Password:</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={{ width: '100%', padding: '8px', boxSizing: 'border-box' }}
            />
          </div>
          <button
            type="button"
            onClick={handleUpdateDetails}
            style={{
              backgroundColor: '#4caf50',
              color: 'white',
              padding: '10px',
              border: 'none',
              cursor: 'pointer',
            }}
          >
            Update Details
          </button>
          <div>
            <label htmlFor="deleteConfirmation" style={{ fontWeight: 'bold' }}>Type 'DELETE' to confirm:</label>
            <input
              type="text"
              id="deleteConfirmation"
              value={deleteConfirmation}
              onChange={(e) => setDeleteConfirmation(e.target.value)}
              style={{ width: '100%', padding: '8px', boxSizing: 'border-box' }}
            />
          </div>
          <button
            type="button"
            onClick={handleDeleteAccount}
            style={{
              backgroundColor: '#ff0000',
              color: 'white',
              padding: '10px',
              border: 'none',
              cursor: 'pointer',
            }}
          >
            Delete Account
          </button>
        </form>
      ) : (
        <p>Please log in to view and update your details.</p>
      )}
    </div>
  );
};

export default EditDetails;
