import React, { useState, useEffect } from 'react';
import { useAuth } from '../hooks/useAuth';
import { updateUserDetails } from '../api/auth';

const EditDetails = () => {
  const { user, isLoggedIn } = useAuth();
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    if (isLoggedIn && user) {
      setUsername(user.username);
      setEmail(user.email);
    }
  }, [isLoggedIn, user]);

  const handleUpdateDetails = async () => {
    try {
      const updatedData = {
        username: username,
        email: email,
        password: password,
      };

      await updateUserDetails(user.id, updatedData);
      console.log('User details updated successfully');
      // You can add additional logic or redirect the user after updating details
    } catch (error) {
      console.error('Error updating user details:', error.message);
      // Handle errors or display a user-friendly message
    }
  };

  return (
    <div className="edit-details-container">
      <h1>Welcome to Edit Details Page</h1>
      {isLoggedIn ? (
        <form className="edit-details-form">
          <div>
            <label htmlFor="username">Username:</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="email">Email:</label>
            <input
              type="text"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <button type="button" onClick={handleUpdateDetails}>
            Update Details
          </button>
        </form>
      ) : (
        <p>Please log in to view and update your details.</p>
      )}
    </div>
  );
};

export default EditDetails;
