import React, { useState, useEffect } from 'react';
import { useAuth } from '../hooks/useAuth';
import { getJobListingsByUserId } from '../api/jobs';

const MyJobs = () => {
  const { user, isLoggedIn } = useAuth();
  const [jobListings, setJobListings] = useState([]);

  useEffect(() => {
    const fetchJobListings = async () => {
      try {
        if (isLoggedIn && user) {
          // Use the user ID to fetch job listings
          const jobListings = await getJobListingsByUserId(user.id);
          setJobListings(jobListings);
        }
      } catch (error) {
        console.error('Error fetching job listings:', error.message);
        // Handle errors or display a user-friendly message
      }
    };

    fetchJobListings();
  }, [isLoggedIn, user]);

  return (
    <div>
      <h1>Welcome to My Jobs Page</h1>
      {isLoggedIn ? (
        <div>
          {jobListings.length > 0 ? (
            <ul className="job-listings">
              {jobListings.map((job) => (
                <li key={job.id} className="job-box">
                  <h2 className="job-title">{job.Title}</h2>
                  <p className="job-company">{job.Company}</p>
                  <p className="job-description">{job.Description.length > 50 ? `${job.Description.substring(0, 50)}...` : job.Description}</p>
                  <div className="job-likes-dislikes">
                    <div className="job-likes">
                      üëç {job.LikesCount}
                    </div>
                    <div className="job-dislikes">
                      üëé {job.DislikesCount}
                    </div>
                  </div>
                  {/* Add more details as needed */}
                </li>
              ))}
            </ul>
          ) : (
            <p>No job listings found.</p>
          )}
        </div>
      ) : (
        <p>Please log in to view your job listings.</p>
      )}
    </div>
  );
};

export default MyJobs;
