import React, { useState, useEffect } from 'react';
import { FaSpinner } from 'react-icons/fa';

const DisplayResume = () => {
  const userId = 'JTJmdXNlcnByb2ZpbGVzY29udGFpbmVyJTJmNjM4MzcyNTMxNDIzMjQ1OTY4';
  const functionUri = 'https://functionappuserresumes.azurewebsites.net/api/HttpTriggerGetResume?code=3xBINMjx9M_ybw4gII-HTDCL5ejk46kuzkfwebsa1gMpAzFu5xSR0w==';

  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFiles = async () => {
      try {
        const response = await fetch(`${functionUri}&userId=${userId}`);
        if (!response.ok) {
          throw new Error(`Error fetching files: ${response.statusText}`);
        }

        const data = await response.json();
        console.log('Data received:', data); // Log the received data
        setFiles(data.files);
      } catch (error) {
        console.error('Error fetching files:', error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchFiles();
  }, [userId, functionUri]);

  // Helper function to determine file type and content type based on extension
  function getFileType(fileName) {
    const extension = fileName.split('.').pop().toLowerCase();
    console.log('Extension:', extension);
    switch (extension) {
      case 'pdf':
        return 'application/pdf';
      case 'audio':
        return 'audio/mov';
      case 'video':
        return 'video/mp4';
      case 'txt':
        return 'text/plain';
      default:
        return 'application/octet-stream';
    }
  }

  // Separate files into arrays based on file type
  const pdfFiles = files.filter((file) => getFileType(file.fileName) === 'application/pdf');
  const videoFiles = files.filter((file) => getFileType(file.fileName) === 'video/mp4');
  const audioFiles = files.filter((file) => getFileType(file.fileName) === 'audio/mov');
  const textFiles = files.filter((file) => getFileType(file.fileName) === 'text/plain');

  // Concatenate the arrays to display PDFs first, then videos, audio files, and text files
  const orderedFiles = pdfFiles.concat(videoFiles, audioFiles, textFiles);

  return (
    <div style={{ maxWidth: '800px', margin: '20px auto 0', position: 'relative' }}>
      {loading && (
        <div style={{ textAlign: 'center', marginBottom: '20px' }}>
          <FaSpinner size={40} className="loading-icon" />
        </div>
      )}

      {orderedFiles.length === 0 && !loading && <p>No files available.</p>}

      {orderedFiles.map((file, index) => (
        <div key={index} style={{ border: '2px solid #ccc', borderRadius: '8px', marginBottom: '20px', padding: '20px' }}>
          <h2 style={{ marginBottom: '10px' }}>{getFileType(file.fileName)}</h2>

          {file.fileType === 'audio/mov' ? (
            <audio controls>
              <source src={`data:${file.contentType};base64,${file.content.toString('base64')}`} type={file.contentType} />
              Your browser does not support the audio element.
            </audio>
          ) : (
            <iframe
              title={`File-${index}`}
              src={`data:${file.contentType};base64,${file.content.toString('base64')}`}
              width="100%"
              height="500px"
              style={{ border: 'none' }}
              allow="autoplay"
            />
          )}
        </div>
      ))}
    </div>
  );
};

export default DisplayResume;
