import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../hooks/useAuth'; // Make sure to update the path

const EditDetails = () => {
  const { user, isLoggedIn } = useAuth();
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    // Load user details when component mounts
    if (isLoggedIn && user) {
      setUsername(user.username);
      setEmail(user.email);
      // Note: You might not want to fetch and display the password for security reasons
      // setPassword(user.password);
    }
  }, [isLoggedIn, user]);

  const handleUpdateDetails = async () => {
    try {
      const response = await axios.put(`http://localhost:5000/api/users/${user.id}`, {
        username: username,
        email: email,
        password: password,
      });

      console.log('User details updated successfully:', response.data);
      // You can add additional logic or redirect the user after updating details
    } catch (error) {
      console.error('Error updating user details:', error.response.data);
      // Handle errors or display a user-friendly message
    }
  };

  return (
    <div>
      <h1>Welcome to Edit Details Page</h1>
      {isLoggedIn ? (
        <form>
          <div>
            <label htmlFor="username">Username:</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="email">Email:</label>
            <input
              type="text"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <button type="button" onClick={handleUpdateDetails}>
            Update Details
          </button>
        </form>
      ) : (
        <p>Please log in to view and update your details.</p>
      )}
    </div>
  );
};

export default EditDetails;
