import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../hooks/useAuth';

const DisplayResume = () => {
  const [filesMetadata, setFilesMetadata] = useState([]);
  const { user } = useAuth();
  // console.log("userrrrr",user)

  useEffect(() => {
    const fetchFilesMetadata = async () => {
      const userId = user.id
      
      console.log("user id", userId)
      try {

        if (!userId) {
          console.error('User ID is not available.');
          return;
        }

        const response = await axios.get(
          `https://functionappuserresumes.azurewebsites.net/api/HttpTriggerGetResume?code=3xBINMjx9M_ybw4gII-HTDCL5ejk46kuzkfwebsa1gMpAzFu5xSR0w==?userId=${userId}`
        );

        setFilesMetadata(response.data);
      } catch (error) {
        console.error('Error fetching files metadata:', error.message);
      }
    };

    fetchFilesMetadata();
  }, []);

  return (
    <div>
      <h1>Display Resumes</h1>
      <ul>
        {filesMetadata.map((file) => (
          <li key={file.mediaID}>
            <strong>User ID:</strong> {file.userId}, <strong>Media ID:</strong> {file.mediaID}, <strong>File Type:</strong> {file.fileType},{' '}
            <strong>Timestamp:</strong> {file.timestamp},{' '}
            <a href={file.blobUrl} target="_blank" rel="noopener noreferrer">
              View File
            </a>
            <br />
            <strong>File Content:</strong> <pre>{file.content}</pre>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default DisplayResume;
