import React, { useState, useEffect } from 'react';

const DisplayResume = () => {
  const userId = 'JTJmdXNlcnByb2ZpbGVzY29udGFpbmVyJTJmNjM4MzcxNjc0MzA1ODY1NDUx'; // Replace with the actual user ID
  const functionUri = 'https://functionappuserresumes.azurewebsites.net/api/HttpTriggerGetResume?code=3xBINMjx9M_ybw4gII-HTDCL5ejk46kuzkfwebsa1gMpAzFu5xSR0w==';

  const [files, setFiles] = useState([]);

  useEffect(() => {
    const fetchFiles = async () => {
      try {
        const response = await fetch(`${functionUri}&userId=${userId}`);
        if (!response.ok) {
          throw new Error(`Error fetching files: ${response.statusText}`);
        }

        const data = await response.json();
        setFiles(data.files);
      } catch (error) {
        console.error('Error:', error.message);
      }
    };

    fetchFiles();
  }, [userId, functionUri]);

  return (
    <div>
      {files.length === 0 ? (
        <p>Loading...</p>
      ) : (
        files.map((file, index) => (
          <div key={index}>
            <p>File Name: {file.fileName}</p>
            {file.fileType === 'application/pdf' && (
              <iframe
                title={`PDF-${index}`}
                src={URL.createObjectURL(new Blob([Uint8Array.from(atob(file.content), c => c.charCodeAt(0))], { type: 'application/pdf' }))}
                width="100%"
                height="500px"
                style={{ border: 'none' }}
              />
            )}
            {file.fileType === 'audio/mp3' && (
              <audio controls>
                <source src={`data:audio/mp3;base64,${file.content}`} type="audio/mp3" />
                Your browser does not support the audio element.
              </audio>
            )}
            {file.fileType === 'video/mp4' && (
              <video width="100%" height="500px" controls>
                <source src={`data:video/mp4;base64,${file.content}`} type="video/mp4" />
                Your browser does not support the video element.
              </video>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default DisplayResume;
