import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../hooks/useAuth';

const MyJobs = () => {
  const { user, isLoggedIn } = useAuth();
  const [jobListings, setJobListings] = useState([]);

  useEffect(() => {
    const fetchJobListings = async () => {
      try {
        if (isLoggedIn && user) {
          // Use the user ID to fetch job listings
          const response = await axios.get(`http://localhost:5000/api/job-listings/?user_id=${user.id}`);
          setJobListings(response.data);
        }
      } catch (error) {
        console.error('Error fetching job listings:', error.message);
        // Handle errors or display a user-friendly message
      }
    };

    fetchJobListings();
  }, [isLoggedIn, user]);

  return (
    <div>
      <h1>Welcome to My Jobs Page</h1>
      {isLoggedIn ? (
        <div>
          {jobListings.length > 0 ? (
            <ul>
              {jobListings.map((job) => (
                <li key={job.id}>
                  <h2>{job.Title}</h2>
                  <p>{job.Description}</p>
                  <p>Company: {job.Company}</p>
                  {/* Add more details as needed */}
                </li>
              ))}
            </ul>
          ) : (
            <p>No job listings found.</p>
          )}
        </div>
      ) : (
        <p>Please log in to view your job listings.</p>
      )}
    </div>
  );
};

export default MyJobs;
