import React, { useState, useEffect } from 'react';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    profileImage: null, // Store the selected profile image file
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Create FormData object to send as multipart/form-data
      const formDataForBackend = new FormData();
      formDataForBackend.append('username', formData.username);
      formDataForBackend.append('email', formData.email);
      formDataForBackend.append('password', formData.password);
      formDataForBackend.append('profileImage', formData.profileImage);

      // Send POST request to Logic App endpoint
      const response = await fetch(
        'https://prod-05.uksouth.logic.azure.com:443/workflows/5e4c364ae4644ee49c285f8aa5a819c5/triggers/manual/paths/invoke?api-version=2016-10-01&sp=%2Ftriggers%2Fmanual%2Frun&sv=1.0&sig=rhsJCcaNO7LNYR1kgYUSbgop1XpTbfbAG3hpgR0azvQ',
        {
          method: 'POST',
          body: formDataForBackend,
        }
      );

      const result = await response.json();
      console.log('Registration successful:', result);

      // Optionally, you can redirect the user to another page after successful registration
      // history.push('/login');
    } catch (error) {
      console.error('Registration error:', error.message);
    }
  };

  const handleFileChange = (e) => {
    // Update profile image file in state
    setFormData({ ...formData, profileImage: e.target.files[0] });
  };

  useEffect(() => {
    console.log('Register component mounted!');
  }, []);

  return (
    <div>
      <h2>Register</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <label>
          Username:
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={(e) => setFormData({ ...formData, username: e.target.value })}
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
          />
        </label>
        <label>
          Password:
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={(e) => setFormData({ ...formData, password: e.target.value })}
          />
        </label>
        <label>
          Profile Image:
          <input type="file" name="profileImage" onChange={handleFileChange} />
        </label>
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
