import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../hooks/useAuth';

const MyJobs = () => {
  const { user, isLoggedIn } = useAuth();
  const [jobListings, setJobListings] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null);
  const [allJobListings, setAllJobListings] = useState([]);

  useEffect(() => {
    const fetchJobListings = async () => {
      try {
        if (isLoggedIn && user) {
          // Use the user ID to fetch job listings
          const userJobListings = await axios.get(`http://localhost:5000/api/job-listings/?user_id=${user.id}`);
          setJobListings(userJobListings.data);

          // Fetch all job listings
          const allJobListings = await axios.get('http://localhost:5000/api/job-listings/');
          setAllJobListings(allJobListings.data);
        }
      } catch (error) {
        console.error('Error fetching job listings:', error.message);
        // Handle errors or display a user-friendly message
      }
    };

    fetchJobListings();
  }, [isLoggedIn, user]);

  const handleJobClick = (job) => {
    setSelectedJob(job === selectedJob ? null : job);
  };

  return (
    <div>
      <h1>Your Job Listings:</h1>
      <div className="job-listings">
        {jobListings.length > 0 ? (
          jobListings.map((job) => (
            <div key={job.id} className={`job-box ${selectedJob === job ? 'selected' : ''}`} onClick={() => handleJobClick(job)}>
              <h2 className="job-title">{job.Title}</h2>
              <p className="job-company">{job.Company}</p>
              <p className="see-more">View full Job Description...</p>
              {selectedJob === job && (
                <div className="job-details">
                  <p className="job-description">{job.Description}</p>
                  <div className="job-likes-dislikes">
                    <div className="job-likes">
                      👍 {job.LikesCount}
                    </div>
                    <div className="job-dislikes">
                      👎 {job.DislikesCount}
                    </div>
                  </div>
                </div>
              )}
            </div>
          ))
        ) : (
          <p>No job listings found.</p>
        )}
      </div>

      <h1>All Job Listings:</h1>
      <div className="job-listings">
        {allJobListings.length > 0 ? (
          allJobListings.map((job) => (
            <div key={job.id} className={`job-box ${selectedJob === job ? 'selected' : ''}`} onClick={() => handleJobClick(job)}>
              <h2 className="job-title">{job.Title}</h2>
              <p className="job-company">{job.Company}</p>
              <p className="see-more">View full Job Description...</p>
              {selectedJob === job && (
                <div className="job-details">
                  <p className="job-description">{job.Description}</p>
                  <div className="job-likes-dislikes">
                    <div className="job-likes">
                      👍 {job.LikesCount}
                    </div>
                    <div className="job-dislikes">
                      👎 {job.DislikesCount}
                    </div>
                  </div>
                </div>
              )}
            </div>
          ))
        ) : (
          <p>No job listings found.</p>
        )}
      </div>
    </div>
  );
};

export default MyJobs;
