// src/Pages/UserProfile.js
import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth'; // Import the useAuth hook
import defaultProfileImage from '../profile-photo.jpg'; // Replace with your actual image path
import '../styles/UserProfile.css'; // You can create a CSS file for styling

const UserProfile = () => {
  const { user, logout } = useAuth(); // Use the useAuth hook to get user information

  return (
    <div className="user-profile-container">
      <h1>Welcome to User Profile Page</h1>
      {user && (
        <>
          <div className="profile-image-container">
            {/* Replace defaultProfileImage with your user's actual profile image */}
            <img src={defaultProfileImage} alt="Profile" />
          </div>
          <div className="profile-links">
            <Link to="/MyResume">Add Resume</Link>
            <Link to="/DisplayResume">Display Resume</Link>
            <Link to="/NewJobListing">Post Job Listing</Link>
            <Link to="/EditDetails">Edit Details</Link>
            <Link to="/MyJobListings">My Job Listings</Link>
          </div>
          <button onClick={() => console.log('Logout button clicked. User:', user)}>Logout</button>
        </>
      )}
    </div>
  );
};

export default UserProfile;
