// NewJobListing.js

import React, { useState } from 'react';
import { postJobListing } from '../api/jobs';
import { useAuth } from '../hooks/useAuth';

const NewJobListing = () => {
  const [title, setTitle] = useState('');
  const [company, setCompany] = useState('');
  const [description, setDescription] = useState('');
  const { user } = useAuth(); // Use the useAuth hook to get user information


  const handlePostJobListing = async () => {
    try {
      if (!user) {
        console.error('User not logged in.');
        return;
      }

      const response = await postJobListing({
        Title: title,
        Company: company,
        Description: description,
        UserID: user.id, // Include the UserID in the job listing data
      });

      console.log('Job Listing posted successfully:', response);
      // You can add additional logic or redirect the user after posting the job listing
    } catch (error) {
      console.error('Error posting job listing:', error);
      // Handle errors or display a user-friendly message
    }
  };

  return (
    <div style={{ maxWidth: '400px', margin: '0 auto' }}>
      <h1 style={{ textAlign: 'center' }}>Welcome to Job Posting Page</h1>
      <form style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <div>
          <label htmlFor="title" style={{ fontWeight: 'bold' }}>Job Title:</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            style={{ width: '100%', padding: '8px', boxSizing: 'border-box' }}
          />
        </div>
        <div>
          <label htmlFor="company" style={{ fontWeight: 'bold' }}>Company:</label>
          <input
            type="text"
            id="company"
            value={company}
            onChange={(e) => setCompany(e.target.value)}
            style={{ width: '100%', padding: '8px', boxSizing: 'border-box' }}
          />
        </div>
        <div>
          <label htmlFor="description" style={{ fontWeight: 'bold' }}>Job Description:</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            style={{ width: '100%', padding: '8px', boxSizing: 'border-box' }}
          />
        </div>
        <button
          type="button"
          onClick={handlePostJobListing}
          style={{
            backgroundColor: '#4caf50',
            color: 'white',
            padding: '10px',
            border: 'none',
            cursor: 'pointer',
          }}
        >
          Post Job Listing
        </button>
      </form>
    </div>
  );
};

export default NewJobListing;
